package helloworld;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String cname = "Gambhir";
		
		switch (cname)
		{
			case "Gambhir":
			System.out.println("KKR");
			break;
		
			case "Kohli":
			System.out.println("RCB");
			break;
		}
			
	}

}
